mapper.update:
update transportation

    updateTime=now()

        ,origin=#{origin}


        , status=#{status}


        , car_dri_id=#{carDriId}


        ,status=#{status}


where id=#{id};

mapper.select:
select id, user_id, goods_id, origin, status, updateTime, car_dri_id from transportation


        id=#{id}


        and user_id=#{userId}


        and status=#{status}


        and car_dri_id=#{carDriId}
